mapper.select:
SELECT
COUNT ( "application_public_access_client"."id" ) AS "customer_added_count",
create_time :: DATE as "day"
FROM
"application_public_access_client"

    APPLICATION_ID = #{appId}

        and  TO_CHAR(application_public_access_client.create_time, 'YYYY-MM-DD') >= #{query.startTime} 


        and  TO_CHAR(application_public_access_client.create_time, 'YYYY-MM-DD') <= #{query.endTime} 


GROUP BY "day";
