mapper.update:
UPDATE knowledge_vector_${knowledgeId}
SET is_active = #{isActive}
WHERE paragraph_id = #{paragraphId}

mapper.select:
SELECT paragraph_id, score
FROM (
    SELECT DISTINCT ON (paragraph_id) paragraph_id, (2 - (
    embedding <=> CAST (#{referenceEmbedding  } AS vector)))/2 AS score
    FROM EMBEDDING
    WHERE
    knowledge_id IN

        #{id}

    AND is_active=true
) t
WHERE score > #{minScore}
ORDER BY score DESC
LIMIT #{maxResults}
