mapper.update:
UPDATE knowledge_vector_${knowledgeId}
SET is_active = #{isActive}
WHERE paragraph_id = #{paragraphId}

mapper.select:
SELECT paragraph_id, score FROM (SELECT DISTINCT ON (paragraph_id) paragraph_id, score FROM ( SELECT paragraph_id, (2 - (embedding <=> CAST (#{referenceEmbedding } AS vector)))/2 AS score FROM embedding WHERE knowledge_id IN
#{id}
AND is_active=true) TEMP ORDER BY paragraph_id, score DESC) DISTINCT_TEMP WHERE score > #{minScore} ORDER BY score DESC LIMIT #{maxResults}

SELECT paragraph_id, score
    FROM (
    SELECT DISTINCT ON (paragraph_id) paragraph_id, (2 - (embedding <=> CAST (#{referenceEmbedding  } AS vector)))/2 AS score
    FROM (
        SELECT paragraph_id, embedding
        FROM EMBEDDING
        WHERE
        knowledge_id IN

            #{id}

        AND is_active=true
    ) TEMP
    ORDER BY paragraph_id, score DESC
) t
WHERE score > #{minScore}
ORDER BY score DESC
LIMIT #{maxResults}
