mapper.select:
SELECT paragraph_id, score FROM (SELECT DISTINCT ON (paragraph_id) paragraph_id, score FROM ( SELECT paragraph_id, (2 - (embedding <=> CAST (#{referenceEmbedding } AS vector)))/2 AS score FROM embedding WHERE dataset_id IN
#{id}
AND is_active) TEMP ORDER BY paragraph_id, score DESC) DISTINCT_TEMP WHERE score > #{minScore} ORDER BY score DESC LIMIT #{maxResults}
