mapper.select:
SELECT k.*, COALESCE(doc_stats.document_count, 0) AS document_count, COALESCE(doc_stats.char_length, 0) AS char_length, COALESCE(map_stats.application_mapping_count, 0) AS application_mapping_count FROM knowledge k LEFT JOIN ( SELECT d.knowledge_id, COUNT(*) AS document_count, SUM(d.char_length) AS char_length FROM "document" d GROUP BY d.knowledge_id ) doc_stats ON k.ID = doc_stats.knowledge_id LEFT JOIN ( SELECT adm.knowledge_id, COUNT(DISTINCT adm.application_id) AS application_mapping_count FROM application_knowledge_mapping adm GROUP BY adm.knowledge_id ) map_stats ON k.ID = map_stats.knowledge_id
AND k.user_id = #{query.createUser}
AND k.name LIKE '%' || #{query.name} || '%'
AND k.folder_id = #{query.folderId}
AND k.ID IN
#{targetId}
AND k.ID=''
ORDER BY k.create_time DESC
