mapper.select:
SELECT app_or_dataset.*, team_member_permission.member_id, team_member_permission.operate FROM ( SELECT "id", "name", 'DATASET' AS "type", user_id FROM dataset WHERE "user_id" = #{teamId} UNION SELECT "id", "name", 'APPLICATION' AS "type", user_id FROM application WHERE "user_id" =#{teamId} ) app_or_dataset LEFT JOIN ( SELECT * FROM team_member_permission
AND "member_id" = #{memberId}
) team_member_permission ON team_member_permission.target = app_or_dataset."id"

SELECT target
FROM team_member_permission tmp
LEFT JOIN "team_member" tm ON tm.ID = tmp.member_id
WHERE tm.user_id = #{userId}
  AND tmp.auth_target_type = #{type}
  AND tmp.operate @> ARRAY[#{operate}]::varchar[];
